mapper.select:

	SELECT bno, nickname, title, contents, salt, hit_cnt, created_datetime, password			
	FROM tbl_board 
	WHERE bno = #{bno}

mapper.update:

	UPDATE tbl_board
	SET title = #{title}, contents = #{contents}
	WHERE bno = #{bno}


    UPDATE tbl_board
    SET hit_cnt = hit_cnt + 1
    WHERE bno = #{bno};
